gpu_program.raw_data_vs:


#version 120

uniform float radii = 5;
varying vec4 v_uv;

void main()
{
  vec4 point =vec4( radii * normalize(gl_Vertex.xyz), gl_Vertex.w); 
  gl_Position = normalize(gl_ModelViewProjectionMatrix * point);  
  v_uv = gl_MultiTexCoord0;
}

gpu_program.raw_data_ps:

#version 120
uniform sampler2D tex0;
varying vec4 v_uv;
void main (void)
{
  vec2 uv = v_uv.xy;
  uv.y *= -1.0;
  gl_FragColor = texture2D(tex0, uv);
}

gpu_program.raw_data_vs:

#version 120
varying vec4 v_color;
void main()
{
	gl_Position = gl_ModelViewProjectionMatrix * gl_Vertex;		
	v_color = gl_Color;
}

gpu_program.raw_data_ps:

#version 120
varying vec4 v_color;
void main (void)
{
  gl_FragColor = v_color;
}
